mapper.select:
with goods_info as (
select #{item.ordGoodsNo} as goods_no , #{item.ordItemNo} as item_no , #{item.ordNo} as ord_no , #{item.dvGrpNo} as dv_grp_no
) select oci.ord_prgs_scd , oci.ord_no , oci.ord_goods_no , oci.ord_item_no , oci.dv_grp_no from op_clm_info oci inner join goods_info gi on oci.ord_no = gi.ord_no and oci.ord_goods_no = gi.goods_no and oci.ord_item_no = gi.item_no and oci.dv_grp_no = gi.dv_grp_no where oci.ord_clm_tp_cd = 'O' and oci.cncl_cnt < 1 and oci.rtgs_cnt < 1

with clm_info as (
select #{item.ordNo} as ord_no
) select opi.* from op_pay_info opi inner join clm_info oci on oci.ord_no = opi.ord_no where opi.pay_ccd = '10' and clm_no is null

with clm_info as (
select #{item.ordSeq} as ord_seq , #{item.procSeq} as proc_seq , #{item.ordNo} as ord_no
) select oobi.* , '' as ord_no from op_ord_bnf_info oobi inner join op_ord_bnf_rel_info oobri on oobi.ord_bnf_no = oobri.ord_bnf_no inner join clm_info oci on oci.ord_no = oobri.ord_no and oci.ord_seq = oobri.ord_seq and oci.proc_seq = oobri.proc_seq

with clm_info as (
select #{item.dvGrpNo} as dv_grp_no , #{item.ordNo} as ord_no
) select ooci.* from op_ord_cost_info ooci inner join clm_info oci on oci.ord_no = ooci.ord_no and oci.dv_grp_no = ooci.dv_grp_no where ooci.aply_ccd = '10';

with clm_info as (
select #{item.ordSeq} as ord_seq , #{item.procSeq} as proc_seq , #{item.ordNo} as ord_no
) select oobri.* from op_ord_bnf_rel_info oobri inner join clm_info oci on oci.ord_no = oobri.ord_no and oci.ord_seq = oobri.ord_seq and oci.proc_seq = oobri.proc_seq where aply_cncl_ccd = '10'

with goods_info as (
select #{item.ordGoodsNo} as goods_no , #{item.ordItemNo} as item_no , #{item.ordNo} as ord_no , #{item.dvGrpNo} as dv_grp_no
) select oci.* from op_clm_info oci inner join goods_info gi on oci.ord_no = gi.ord_no and oci.ord_goods_no = gi.goods_no and oci.ord_item_no = gi.item_no where oci.ord_clm_tp_cd = 'O' and oci.cncl_cnt = 0 and oci.rtgs_cnt = 0

with goods_info as (
select #{item.ordGoodsNo} as goods_no , #{item.ordItemNo} as item_no , #{item.ordNo} as ord_no , #{item.dvGrpNo} as dv_grp_no
) select oci.* from op_clm_info oci inner join goods_info gi on oci.ord_no = gi.ord_no and oci.ord_goods_no = gi.goods_no and oci.ord_item_no = gi.item_no where oci.ord_clm_tp_cd = 'R' and oci.cncl_cnt = 0 and oci.rtgs_cnt = 0
