mapper.select:
with goods_info as (
select #{item.ordGoodsNo} as goods_no , #{item.ordItemNo} as item_no , #{item.ordNo} as ord_no , #{item.dvGrpNo} as dv_grp_no
) select oci.ord_prgs_scd , oci.ord_no , oci.ord_goods_no , oci.ord_item_no , oci.dv_grp_no from op_clm_info oci inner join goods_info gi on oci.ord_no = gi.ord_no and oci.ord_goods_no = gi.goods_no and oci.ord_item_no = gi.item_no and oci.dv_grp_no = gi.dv_grp_no where oci.ord_clm_tp_cd = 'O' and oci.cncl_cnt < 1 and oci.rtgs_cnt < 1

select
    ooci.*
from op_ord_cost_info ooci
where
    ooci.ord_no = #{ordNo}
    and ooci.aply_ccd = '10';
