mapper.select:
select
    /* PromotionMapper.selectAvailableRestoreCoupon */
  cci.prm_no
, cci.use_strt_dtime as prm_strt_dt
, cci.use_end_dtime as prm_end_dt
from cc_cpn_issue cci
where
  cci.prm_no = #{prmNo}
  and cci.cpn_iss_no = #{couponIssueNo}
  and now() between cci.use_strt_dtime::date and cci.use_end_dtime::date + 1

select /* PromotionMapper.selectAvailablePromtionList */ cpb.prm_no , cpb.prm_nm , cpb.dc_val , cpb.min_pur_amt , cpb.max_dc_amt , cpb.dc_ccd , ccb.cpn_kind_cd , cci.cpn_iss_no , pgb.goods_no from cc_prm_base cpb inner join cc_prm_aply_tgt cpat on cpb.prm_no = cpat.prm_no inner join cc_cpn_issue cci on cpb.prm_no = cci.prm_no and cci.mbr_no = #{mbrNo} inner join cc_cpn_base ccb on ccb.prm_no = cpb.prm_no inner join pr_goods_base pgb on cpat.aply_tgt_no = pgb.goods_no where cpb.use_yn = 'Y' and cci.cpn_use_dt is null and cpb.prm_kind_cd = '20' and now() between cci.use_strt_dtime::date and cci.use_end_dtime::date + 1 and aply_tgt_no in
#{item.goodsNo}
order by cpn_iss_no
